import "./Movie.css";

function Movie({poster_path, title, vote_average}) {
    const imageUrl = `https://www.themoviedb.org${poster_path}`;
    return(
        <div className="Movie">
            <div className="image-section">
                <img alt="img_poster" src={{poster_path}}/>
            </div>
            <div className="info-section">
                <div className="title">{title}</div>
                <div className="vote_average">{vote_average}</div>
            </div>
        </div>
    )
}
export default Movie
