import { useState } from "react";
import "../Movie.css";
import Overview from "../Overview";
import { ImageSection, InfoSection, MovieWrapper, OverviewWrapper, Title, VoteAverage } from "./Movie.style";

function Movie({poster_path, title, vote_average, overview}) {
    const imageUrl = `https://image.tmdb.org/t/p/w1280${poster_path}`;
    const [showOverview, setShowOverview] = useState(false);
    
    const handleMouseEnter = () => {
        setShowOverview(true);
    }
    const handleMouseLeave = () => {
        setShowOverview(false);
    }

    return(            
        <div
        // <MovieWrapper
            onMouseEnter={handleMouseEnter}
            onMouseLeave={handleMouseLeave}
        >
            {/* <div className="Movie"> */}
            <MovieWrapper>
                {/* <div className="image-section">
                    <img alt="img_poster" src={imageUrl}/>
                </div> */}
                <ImageSection>
                    <Image alt="img_poster" src={imageUrl}/>
                </ImageSection>

                {/* <div className="info-section">
                    <div className="title">{title}</div>
                    <div className="vote_average">{vote_average}</div>
                </div> */}
                <InfoSection>
                    <Title>{title}</Title>
                    <VoteAverage>{vote_average}</VoteAverage>
                </InfoSection>

                {showOverview && (
                    // <div className="overview-wrapper">
                    //     <Overview
                    //         title={title}
                    //         overview={overview}
                    //     />        
                    // </div>
                    <OverviewWrapper>
                        <Overview title={title} overview={overview}/>
                    </OverviewWrapper>
                )}
            {/* </div> */}
            </MovieWrapper>
        {/* </MovieWrapper> */}
        </div>
    )
}
export default Movie
